# Tests for the Mock Server using RSpec \require_relative '../src/server' \describe MockServer do \it 'returns a mock response' do \response = get('/mock/1') \expect(last_response.status).to eq(200) \expect(JSON.parse(last_response.body)).to include('data') \end \it 'returns not found for non-existing mock' do \response = get('/mock/999') \expect(last_response.status).to eq(404) \end
